'use strict';
/**
 * @ngdoc directive
 * @name userList
 *
 * @description
 * User List  Directive To List all the User and Select one for Disassociate or Edit
 */
angular.module('hillromvestApp')
  .directive('userList', function() {
    return {
      templateUrl: 'scripts/modules/admin/hill-rom-user/directives/list/list.html',
      restrict: 'E',
      scope: {
        onSelect: '&',
        onCreate: '&',
        userStatus: '=userStatus'
      },
      link: function(scope) {
        var user = scope.user;
        scope.$on('resetList', function () {
          scope.searchUsers();
        })
      },
      controller: ['$scope', '$timeout', '$state', 'UserService', 'searchFilterService', function($scope, $timeout, $state, UserService, searchFilterService) {
        var searchOnLoad = true;
        $scope.init = function() {
          $scope.users = [];
          $scope.currentPageIndex = 1;
          $scope.perPageCount = 10;
          $scope.pageCount = 0;
          $scope.total = 0;
          $scope.sortOption ="";
          $scope.showModal = false;
          $scope.sortIconDefault = true;
          $scope.sortIconUp = false;
          $scope.sortIconDown = false;
          $scope.searchItem = "";
          $scope.searchFilter = searchFilterService.initSearchFiltersForHCP();
          $scope.searchUsers();
        };

        var timer = false;
        $scope.$watch('searchItem', function () {
          if(!searchOnLoad){
            if (timer) {
              $timeout.cancel(timer)
            }
            timer= $timeout(function () {
                $scope.searchUsers();
            },1000)
          }
        });

        /**
         * @ngdoc function
         * @name selectUser
         * @description
         * Function to select the User from the List suggested on search
         */
        $scope.selectUser = function(user) {
          $state.go('hillRomUserEdit', { userId: user.id });
        };

        $scope.createUser = function() {
          $state.go('hillRomUserNew');
        };

        /**
         * @ngdoc function
         * @name sortList
         * @description
         * Function to Search User on entering text on the textfield.
         */
        $scope.searchUsers = function(track) {
          if (track !== undefined) {
            if (track === "PREV" && $scope.currentPageIndex > 1) {
              $scope.currentPageIndex--;
            } else if (track === "NEXT" && $scope.currentPageIndex < $scope.pageCount) {
              $scope.currentPageIndex++;
            } else {
              return false;
            }
          } else {
            $scope.currentPageIndex = 1;
          }
          var filter = searchFilterService.getFilterStringForHCP($scope.searchFilter);
          var url = 'api/user/search?searchString=';
          UserService.getUsers(url, $scope.searchItem, $scope.sortOption, $scope.currentPageIndex, $scope.perPageCount, filter).then(function(response) {
            $scope.users = response.data;
            $scope.total = response.headers()['x-total-count'];
            $scope.pageCount = Math.ceil($scope.total / 10);
            searchOnLoad = false;
          }).catch(function(response) {});
        };

        $scope.sortType = function(){
          console.log('hello');
          if($scope.sortIconDefault){
            $scope.sortIconDefault = false;
            $scope.sortIconUp = false;
            $scope.sortIconDown = true;
          }
          else if($scope.sortIconDown){
            $scope.sortIconDefault = false;
            $scope.sortIconDown = false;
            $scope.sortIconUp = true;
          }
          else if($scope.sortIconUp){
            $scope.sortIconDefault = false;
            $scope.sortIconUp = false;
            $scope.sortIconDown = true;
          }
        };
        $scope.searchOnFilters = function(){    
          $scope.searchUsers();
        };

        $scope.init();
      }]
    };
  });

